import React from 'react'
import { StyleSheet, Text, View , KeyboardAvoidingView , Animatiable} from 'react-native'
import {Input , Button} from 'react-native-elements'
import Icon from 'react-native-vector-icons/FontAwesome';
import Axios from 'axios';
import {CountryDropdown , RegionDropdown , CountryRegionData } from 'react-country-region-selector'
const Register = () => {
    const [firstName, setFirstName] = React.useState('')
    const [lastName, setLastName] = React.useState('')
    const [email, setEmail] = React.useState('')
    const [password, setPassword] = React.useState('')
    const [confirmPassword, setConfirmPassword] = React.useState('')
    const [address, setAddress] = React.useState('')
    const [phoneNumber, setPhoneNumber] = React.useState('')
    const [country, setCountry] = React.useState('')
    const [region, setRegion] = React.useState('')
    const [passMsg , setPassMsg] = React.useState('')
    const [emailError , setEmailError] = React.useState('')
    const [firstNameError , setFirstNameError] = React.useState('')
    const emailRegex = /\S+@\S+\.\S+/
    const mailValidation = (event) =>{
       // event.preventDefault()
        if(emailRegex.test(email))
        {
            
            alert('email is correct regex')
            
        }
        else{
            setEmailError('please enter a valid mail')
            setTimeout(() => {
                setEmailError('')
            }, 4000);
        }
    }
    const maxLength = 10
    const minLength = 4
    const validation = (event) => {
        if(firstName.length != 0)
        {
            if(firstName.length <= 3){
                setFirstNameError('First name must be greater than 3')
                setTimeout(() => {
                    setFirstNameError('')
                }, 4000);
            }
            else if(firstName.length > 3 && firstName.length <=10){
                setFirstNameError('First name must be less than or equal to 10')
                setTimeout(() => {
                    setFirstNameError('')
                }, 4000);
            }
            mailValidation()
        }
        else{
            alert('enter the name first')
        }
    }

//     const submitButton = (e) => {
//     //sending the data to backend 
//     e.preventDefault()
//     Axios.post('http://localhost:3000/post/api' , {
//         firstName : firstName,
//         lastName : lastName,
//         email : email,
//         password : password,
//         confirmPassword : confirmPassword,
//         address : address,
//         phoneNumber : phoneNumber,
//         country : country,
//     }).then(function(response){
//         if(response.data === 'mail is exist'){setMessage(response.data)}
//         else if(response.data === 'password does not match'){setPassMsg(response.data)}
//         console.log(response.data)}).catch((err)=>{console.log(err.message)})
// }

    const submitButton = (event) => {
        event.preventDefault()
        mailValidation()
    }
    return (
        <KeyboardAvoidingView behavior='padding' style={styles.overAllContainer}>
            <Text style={styles.header}>ZAGA REGISTRATION</Text>
            <View style={styles.inputContainer}>
                <Input 
                placeholder='First Name'
                value = {firstName}
                onChangeText={text => setFirstName(text)}
                />
                <View style={{paddingLeft:10}}><Text>{firstNameError}</Text></View>
                <Input 
                value = {lastName}
                onChangeText={text => setLastName(text)}
                placeholder='Last Name'/>
                <Input
                placeholder='Email'
                keyboardType='email-address'
                value ={email}
                onChangeText={text => setEmail(text)}/>
                <View style={{paddingLeft:10}}><Text>{emailError}</Text></View>
                <Input
                 secureTextEntry
                 placeholder='Password'
                 value = {password}
                 onChangeText={text => setPassword(text)}
                />
                <Input 
                secureTextEntry
                placeholder='Confirm Password'
                value = {confirmPassword}
                onChangeText={text => setConfirmPassword(text)}
                />
                <View style={{paddingLeft:10}}><Text>{passMsg}</Text></View>
                <Input 
                placeholder='Address'
                value = {address}
                onChangeText={text => setAddress(text)}
                />
                <Input
                 placeholder='Phone Number'
                 keyboardType='numeric'
                 value = {phoneNumber}
                 onChangeText={text => setPhoneNumber(text)}
                 />
                 <View style={{width:300 , padding: 10,}}>
                    <CountryDropdown style={{paddingTop:10,marginBottom:10}}
                    value={country}
                    onChange={val=>setCountry(val)}
                    />
                    <RegionDropdown style={{paddingTop:10}}
                    country={country}
                    value={region}
                    onChange={val=>setRegion(val)}
                    />
                 </View>
                
            </View>
            <View style={{height:20}}/>
            <Button style={styles.button}
            icon={
                <Icon
                  name="arrow-right"
                  size={15}
                  color="white"
                />}
            title='Register' onPress={validation} />
        </KeyboardAvoidingView>
    )
}

export default Register

const styles = StyleSheet.create({
    overAllContainer:{
        flex:1,
        padding:10,
        alignItems: 'center',
        justifyContent:'center'
    },
    header:{
        fontSize:20,
        fontWeight:'bold',
        marginBottom:40,
        color : '#22A0B6',
    },
    inputContainer:{
        width:300,
    },
    button:{
        width:200,
    },
})
